    percent = null;
    if (null != start && null != stop && start != stop) {
        curr = now - start;
        percent = integer(curr * 100 / (stop - start));
        if (percent < 0) {
            percent = 0;
        } else if (percent > 100) {
            percent = 100;
        }
    }

    /* this ugly condition seems to be the only chance to get it working */
    if ((null != stop && stop > 0 && now >= stop) ||
        (null != percent && 100 == percent))
    {
