" + getItemInfo(index , "description");
					executeScript("setEnvValue");
                	res = "false";
                {/if}
            } else if ( key == keyPgUp ) {
                setFocusItemIndex(0);
            } else if ( key == keyPgDn ) {
                setFocusItemIndex(getPageInfo("itemCount") - 1);
            } else {
                res = "false";
            }
        } else {
            if ( key == keyVStop || key == keyReturn ) {
                executeScript("videoStop");
            } else if ( key == keyDisplay ) {
                if (0 != osdPos) {
                    setFocusItemIndex(playingChannel);
                    executeScript("osdShow");
                } else {
                    executeScript("osdHide");
                }
            } else if (0 != osdPos && ( key == keyUp || key == keyDown || key == keyLeft || key == keyRight || key == keyEnter || key == keyPgUp || key == keyPgDn )) {
                /* show OSD if it's not shown yet and do nothing more */
                executeScript("osdShow");
            } else if (0 == osdPos) {

                executeScript("osdShow");

                if ( key == keyUp || key == keyDown ) {
                    res = "false";
                } else if ( key == keyLeft ) {
                    postMessage(keyUp);
                } else if ( key == keyRight ) {
                    postMessage(keyDown);
                } else if ( key == keyEnter ) {
                	if(playingChannel != index) {
                    	executeScript("videoStart");
                    } else {
                    	executeScript("osdHide");
                    }
                } else if ( key == keyPgUp ) {
                    setFocusItemIndex(0);
                } else if ( key == keyPgDn ) {
                    setFocusItemIndex(getPageInfo("itemCount") - 1);
                } else {
                    res = "false";
                }
            } else {
                res = "false";
            }
        }
        if ("true" == res) {
            redrawDisplay();
        }
