rss.onEnter:
checkIndex = 0;
subMenuString = null;
subMenuString = pushBackStringArray(subMenuString, "Пауза");
subMenuString = pushBackStringArray(subMenuString, "Продолжить загрузку");
subMenuString = pushBackStringArray(subMenuString, "Удаление загрузки");
subMenuString = pushBackStringArray(subMenuString, "Очистить список");
subMenuSize = 4;
setFocusItemIndex(0);
setItemFocus(0);
redrawDisplay();

mediaDisplay.onUserInput:
handled = "false";
userInput = currentUserInput();
focusIndex = getFocusItemIndex();
if ("enter" == userInput || "ENTR" == userInput ) {
	data = getStringArrayAt(subMenuString, focusIndex);
	setReturnString(focusIndex);
	postMessage("return");
	handled = "true";
}
else if ("left" == userInput || "right" == userInput|| "L" == userInput|| "R" == userInput) {
	handled = "true";
}
handled;
