BackgroundDark.script:
ret = "yes";
if(CurrentAP=="IMSAP"){
	if(CurrentType=="Photo"){
		ret = "no";
	}else if(CurrentType=="Video"){
		ret = "no";
	}
}else if(CurrentAP=="VideoPlayback"){
	ret = "no";
}else if(CurrentAP=="PhotoPlaybackAP"){
	ret = "no";
}

