text.script:
if(watchInfo != "")
	watchInfo;
else if(pageItem == 0)
	"Выберите интересующий вас раздел...";
else 
	"Кнопка 'ОК' добавляет торрент в список для закачки...";

mediaDisplay.onUserInput:
input = currentUserInput();
ret = "false";
if (input == "return" || input="left" || input = "RET" || input = "L")
{
	if(pageItem == 1)
	{	
		sectionItem = "";
		executeScript("load_Root");
		ret = "true";
	}
} else if(input == "up" || input = "down" || input = "U" || input = "D")
{
	if(watchInfo != "")
	{
		watchInfo = "";
		redrawDisplay();
		ret = "true";
	}
}
ret;

item_template.onClick:
itemLink = getStringArrayAt(itemLinkArray , -1);
if(pageItem == 0)
{
	itemIndex = getFocusItemIndex();
	sectionItem = getStringArrayAt(itemTitleArray , -1);
	executeScript("load_Feeds");
} 
else if(pageItem == 1)
	executeScript("watch_Feed");
null;

rss.load_Root:
pageItem = 0;
itemTitleArray = null;
itemLinkArray = null;
watchInfo = "";

itemTitleArray  = pushBackStringArray(itemTitleArray, "Торренты за последние 24 часа");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Кино");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=1");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Сериалы");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=4");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Телевизор");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=6");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Мультипликация");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=7");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Книги");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=5");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Музыка");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=2");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Игры");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=8");
itemTitleArray  = pushBackStringArray(itemTitleArray, "Софт");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=9");	
itemTitleArray  = pushBackStringArray(itemTitleArray, "Другое");
itemLinkArray  = pushBackStringArray(itemLinkArray, "http://www.opensharing.org/rss.php?cat=3");
itemSize = 10;

setFocusItemIndex(itemIndex);
setItemFocus(0);
redrawDisplay();

rss.load_Feeds:
itemTitleArray = null;
itemLinkArray = null;
watchInfo = "";
dlok = loadXMLFile(itemLink);
if (dlok != null)
{			
	itemSize = getXMLElementCount("rss", "channel", "item");
	print("itemSize = ", itemSize);

	if(itemSize > 0)
	{
		itemCount = 0;		
		while(1)
		{
			itemTitle  = getXMLText("rss", "channel", "item", itemCount, "title");
			print("itemTitle = ", itemTitle);
			itemTitleArray  = pushBackStringArray(itemTitleArray, itemTitle);
			itemLink  = getXMLText("rss", "channel", "item", itemCount, "link");
			print("itemLink = ", itemLink);
			itemLinkArray  = pushBackStringArray(itemLinkArray, itemLink);
			itemCount += 1;
			if(itemCount == itemSize)
				break;
		}
		setFocusItemIndex(0);
		setItemFocus(0);
		redrawDisplay();
		pageItem = 1;
	}
}

rss.watch_Feed:
url = "http://127.0.0.1/cgi-bin/torrents_xml.cgi?addtorrent,,";
dlok = loadXMLFile(url + itemLink);
if (dlok != null)
{			
	watchInfo =  getXMLText("return", "item", 0 , "title");
	redrawDisplay();
}
